/**
 * Mock wrapper library that re-exports from external-lib.
 * This simulates patterns like @floating-ui/react-dom re-exporting from @floating-ui/utils.
 */

// Re-export types from the underlying library
export type { Rect, Point, Padding } from 'external-lib';

/**
 * A wrapper-specific type that uses the re-exported Rect.
 */
export declare type BoundingRect = Rect & { label?: string };
