/**
 * Mock external library types for testing.
 *
 * This simulates the pattern found in libraries like @floating-ui/utils
 * where a type alias wraps another utility type.
 * 
 * Uses `declare type` syntax to match real npm package .d.ts output.
 */

// Internal utility types (not exported directly)
declare type Coords = { x: number; y: number };
declare type Dimensions = { width: number; height: number };

// Prettify is a common pattern that "cleans up" intersection types for better display
declare type Prettify<T> = { [K in keyof T]: T[K] } & {};

/**
 * A rectangle with x, y coordinates and width, height dimensions.
 *
 * TypeScript will resolve this to Prettify<Coords & Dimensions>,
 * but consumers should see "Rect" in their type information.
 */
export declare type Rect = Prettify<Coords & Dimensions>;

/**
 * A simple point type.
 */
export declare type Point = Coords;

/**
 * A size type with width and height.
 */
export declare type Size = Dimensions;

// SideObject for padding (like @floating-ui/utils)
declare type SideObject = {
	top: number;
	right: number;
	bottom: number;
	left: number;
};

/**
 * Padding can be a number or a partial side object.
 * This is a UNION type that should be EXPANDED (not collapsed to just "Padding").
 */
export declare type Padding = number | Prettify<Partial<SideObject>>;
